mapper.select:
/* modelSql.modelsList */ SELECT id
, name
, progress_state , create_date , filename , filepath , command , progress_start_date , progress_end_date , validation_summary FROM
"DL_MODEL"
"ML_MODEL"
WHERE delete_flag = false AND project_id = #{model.projectSequenceFk4}
AND ml_preprocessed_data_id = #{model.preprocessedDataSequenceFk2}
ORDER BY id DESC

mapper.insert:
		/* modelSql.insertModel */
		INSERT INTO 
   			"ML_MODEL"(
   				id
   				, command
   				, name
   				, create_date
   				, progress_state
   				, progress_start_date
   				, ml_original_data_id
   				, ml_preprocessed_data_id
				, project_id
   			) VALUES (
   				#{model.modelSequencePk}
   				,#{model.command}
   				,#{model.name}
   				,#{model.createDatetime}
   				,#{model.progressState}
   				,#{model.progressStartDatetime}
   				,#{model.originalDataSequenceFk1}
   				,#{model.preprocessedDataSequenceFk2}
				,#{model.projectSequenceFk4}
   			)
