mapper.select:
/* projectSql.projects */ SELECT id , name , description , user_id , create_date , to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') as "createDateOrder" FROM "PROJECT" AS project
name ILIKE '%'||#{value}||'%'
description ILIKE '%'||#{value}||'%'
user_id ILIKE '%'||#{value}||'%'
to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') ILIKE '%'||#{value}||'%'
(name ILIKE '%'||#{value}||'%' OR description ILIKE '%'||#{value}||'%' OR to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') ILIKE '%'||#{value}||'%'
OR user_id ILIKE '%'||#{value}||'%'
)
AND user_id = #{currentId}
AND delete_flag = false AND type = #{type}
ORDER BY "createDateOrder" DESC LIMIT 10 OFFSET #{pageNo}

/* projectSql.checkProjectName */
SELECT count(id)
FROM "PROJECT"
WHERE name = #{name}
	AND delete_flag = FALSE
	AND type = #{type}

		AND id NOT IN(#{id})

mapper.insert:
		/* projectSql.insertProject */
		INSERT INTO 
			"PROJECT"(
				name
				, description
				, create_date
				, user_id
				, type
			)VALUES(
           		#{name}
           		, #{description}
           		, NOW()
           		, #{userId}
           		, #{type}
          	)

mapper.select:
/* projectSql.getProjectCount */ SELECT COUNT(id) FROM "PROJECT"
name ILIKE '%'||#{value}||'%'
description ILIKE '%'||#{value}||'%'
user_id ILIKE '%'||#{value}||'%'
to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') ILIKE '%'||#{value}||'%'
(name ILIKE '%'||#{value}||'%' OR description ILIKE '%'||#{value}||'%' OR to_char(create_date, 'YYYY-MM-DD HH24:MI:SS') ILIKE '%'||#{value}||'%'
OR user_id ILIKE '%'||#{value}||'%'
)
AND user_id = #{currentId}
AND delete_flag = false
AND type = #{type}
